var dial = function() {

    function e(b, d) {
        setTimeout(function() {
            "scrolling" == a.state && (":end" != b ? a.output += b : a.state = "waiting", a.chars.shift());
        }, d);
    }

    var a = {
        state: "waiting",
        speed: 50,
        chars: [],
        output: ""
    };

    return {
        print: function() {
            console.log(a.state);
            console.log(a.speed);
            console.log(a.chars);
            console.log(a.output);
        },
        dat: a,
        state: function() {
            return a.state;
        },
        add: function(b) {
            for (var d = [], c = 0; c < arguments.length; ++c) {
                d[c - 0] = arguments[c];
            }
            for (var e in d) {
                c = d[e];
                for (var f = 0; f < c.length; f++) {
                    a.chars.push(c.charAt(f));
                }
                a.chars.push(":end");
            }
        },
        start: function() {
            if ("waiting" == a.state || "finished" == a.state) {
                a.state = "scrolling";
                for (var b in a.chars) {
                    if (e(a.chars[b], b * a.speed), ":end" == a.chars[b]) {
                        break;
                    }
                }
            }
        },
        stop: function() {
            a.state = "waiting";
        },
        clear: function() {
            a.output = "";
        },
        speed: function(b) {
            a.speed = b;
        },
        output: function() {
            return a.output;
        }
    };
}();